// SECTION FOR DEVELOPPEMENT PAGES
.nndvpContent{
    display: flex;
    flex-direction: column;
    height: 100vh;
    align-items: center;
    background-image: linear-gradient(to top, #a18cd1 0%, #fbc2eb 100%);
}
// END OF SECTION FOR DEVELOPPEMENT PAGES

$blue-primary: #5990de;
$orange-primary: #ffa100;
$bckground-primary: #EEEEEE;
$color-txt-primary: #585858;
$color-txt-secondary: #212121;
$color-txt-orange: #FFCA28;

@keyframes toggleUpDown {
    from {
        transform: translateY(-5px);
    } to {
        transform: translateY(5px);
    }
}

* {
    margin: 0;
    padding: 0;
    font-family: 'Noto sans', sans-serif;
}


// animation of the burger
.animCross .line1 {
    transform: rotate(-45deg)  translateY(0.9rem);
}
.animCross .line2 {
    opacity: 0;
}
.animCross .line3 {
    transform: rotate(45deg) translateY(-0.9rem);
}
// end of animations

.testButton {
    cursor: pointer;
    color: black;
    background-color: $orange-primary;
    border-radius: 2rem;
    border: solid 2px $orange-primary;
    transition: all 500ms;
    &:hover {
        text-decoration: none;
        color: $blue-primary;
        background-color: white;
        border: solid 2px $blue-primary;
    }
}
.submitButton {
    cursor: pointer;
    color: black;
    background-color: $orange-primary;
    border-radius: 2rem;
    border: solid 2px $orange-primary;
    transition: all 500ms;
    &:hover {
        text-decoration: none;
        color: $blue-primary;
        background-color: white;
        border: solid 2px $blue-primary;
    }
}
.mapTestButton {
    cursor: pointer;
    color: black;
    background-color: $orange-primary;
    border-radius: 2rem;
    border: solid 2px $orange-primary;
    transition: all 500ms;
    &:hover {
        text-decoration: none;
        color: $blue-primary;
        background-color: white;
        border: solid 2px $blue-primary;
    }
}
.borderBottom {
    border-bottom: 2px solid $blue-primary;
}
.chevron {
    height: 50px;
    & div {
        height: 2px;
        width: 30px;
    }
}

.containerPopup {
    position: fixed;
    z-index: 2;
    top: 0;
    height: 100vh;
    width: 100%;
    background-color: rgb(0, 0, 0, 0.8);
    display: flex;
    justify-content: center;
    align-items: center;
    & i {
        position: absolute;
        z-index: 1;
        top: 0;
        right: 0;
        background-color: black;
        color: white;
        padding: 1rem;
        cursor: pointer;
        transition: color 400ms;
        &:hover {
            color: $blue-primary;
        }
    }
    & .wrapperPopup {
        text-align: center;
        position: fixed;
        background-color: white;
        border-radius: 0.5rem;
        & .disclaimer {
            display: flex;
            align-items: center;
            justify-content: center;
            height: 5rem;
            background-color: $blue-primary;
            font-size: 1.5rem;
            color: white;
            border-radius: 0.5rem 0.5rem 0 0;
        }
        & .wrapperInput {
            display: grid;
            grid-template-columns: 1fr;
            gap: 2rem;
            margin: 2rem 3rem;
            & input {
                width: 94%;
                height: 2rem;
                padding-left: 1rem;
            }
        }
        & .wrapperButton {
            display: flex;
            justify-content: center;
            align-items: center;
        }
        & .submitButton {
            padding: 0.75vh 2rem;
        }
    }
}

.navbar {
    &>div {
        max-height: 100%;
    }
    & .wrapperBurger {
        display: none;
    }
    padding: 0rem;
    text-align: center;
    background-color: white;
    & .dropidown-menu {
        display: none;
        list-style: none;
        text-align: left;
        & a {
            text-decoration: none;  
            font-size: 1.25rem;
            color: $color-txt-primary;
            transition: color 300ms;
            &:hover {
                color: $orange-primary;
                text-decoration: none;

            }
        }
    }
    &__item {
        text-decoration: none;  
        display: flex;
        justify-content: center;
        font-size: 1.5rem;
        font-weight: bold;
        color: $color-txt-primary;
        transition: color 300ms;
        &:hover {
            text-decoration: none;
            color: $blue-primary;
        }
    }
    & .testButton {
        text-decoration: none;
        display: block;
        font-size: 1.5rem;
        font-weight: bold;
        padding: 0.5vh 2rem;
    }
}

header {
    // background image remplacé par background blue en l'attente
    background-color: #90CAF9;
    text-align: center;
    display: grid;
    color: #616161;
    & h1 {
        font-size: 2.5rem;
        font-weight: 100;
        color: black;
        margin: 2rem 0;
        font-family: 'Montserrat', sans-serif;
    }
    & div {
        padding: 1rem 0;
    }
}

main {
    & .title {
        color: $color-txt-orange;
        text-align: center;
        border-bottom: solid $color-txt-orange 1px;
        margin: 1rem 10% 2rem 10%;
        & h1 {
            font-family: 'Montserrat', sans-serif;
            font-weight: 100;
        }
        & .chevron {
            padding-top: 1rem;
            cursor: pointer;
            &:hover {
                animation: toggleUpDown 400ms ease-in-out infinite alternate;
            }   
        }
    }
    & .wrapperContent {
        & .line {
            column-gap: 2rem;
            display: grid;
            align-items: center;
            & img {
                max-width: 100%;
            }
            & div {
                & h2 {
                    font-family: 'Montserrat', sans-serif;
                    font-weight: 100;
                    font-size: 2rem;
                    margin-bottom: 0.5rem;
                }
                & p {
                    color: $color-txt-primary;
                    margin: 1rem 0 1rem 1rem;
                    & a {
                        font-size: 0.95rem;
                    }
                }
            }
            & .dropdownArticleButton {
                cursor: pointer;
                color: $blue-primary;
                transition: all 400ms;
                text-align: center;
                & i {
                    color: $orange-primary;
                    opacity: 0;
                    transform: translateY(-7px);
                    transition: all 400ms;
                }
                &:hover {
                    color: $orange-primary;
                    text-decoration: none;
                    & i {
                        opacity: 1;
                        transform: translateY(0px);
                    }
                }
            }
        }
        & .dropdownArticle {
            display: none;
            background-color: #F5F5F5;
            padding: 3rem 20%;
