    background-color: #212121;
    display: grid;
    padding-top: 2rem;
    & .containerLegal {
        & h1 {
            margin:0 0 1rem 0.75rem;
            color: white;
            font-weight: 500;
            font-size: 2rem;
        }
        & .wrapperLink {
            margin: 1rem 0;
            display: grid;
            & a:first-child+a {
                grid-row: 2 / 3;
            }
            & a {
                text-decoration: none;
                color: #E0E0E0;
                border-bottom: solid 1px #BDBDBD;
                padding: 0.5rem;
                transition: all 300ms;
                &:hover {
                    text-decoration: none;
