    & .containerNousContacter {
        & h1 {
            margin:0 0 1rem 0.75rem;
            font-weight: 500;
            font-size: 2rem;
        }
        & h2 {
            font-weight: 400;
            font-size: 1.25rem;
        }
        & p {
            font-size: 1rem;
            margin: 2rem 0;
        }
        & i {
            margin-right: 0.75rem;
        }
        & a {
            text-decoration: none;
            color: #E0E0E0;
            padding: 0.5rem 1rem 1rem 0;
            transition: all 300ms;
            &:hover {
                text-decoration: none;
                color: $blue-primary;
            }
        }
    }
}


// full screen
@media screen and (min-width:1200px) { 
    .containerPopup .wrapperPopup {
        width: 30%;
        height: 63vh;
    }
    .navbar {
        grid-template-rows: 8.5vh;
        height: 8.5vh;
        display: grid;
        grid-template-columns: 1fr 1fr 2fr;
        & .wrapperLink {
            grid-column: 3 / 4;
            display: grid;
            grid-template-columns: repeat(4, 1fr);
        }
        & .wrapperDropidown {
            grid-column: 1 / 3;
            display: grid;
            grid-template-columns: repeat(2, 1fr);
        }
        & .dropidown-menu {
            width: 100%;
            position: absolute; 
            top: 8.5vh; 
            left: 0;
            grid-row: 2 / 3;
            padding: 4rem 0 4rem 15rem;
            background-color: white;
        }
        &__item {
            max-height: 100%;
            padding-top: 2.8vh;
            &:hover {
                border-bottom: 2px solid $blue-primary;
            }
        }
        & .dropidown-button {
            display: grid;
        }
        & .testButton {
            margin: 2vh 3rem 0 0;
        }
    }
    
    header {
        padding: 1rem 2rem 1rem 40%;
        font-size: 1.2rem;
        grid-template-columns: repeat(4, 1fr);
        // positionnement
        & h1 {
            grid-column: 1/5;
        }
        &>div {
            grid-row: 2/3;
            grid-column: 2/4;
            &+div {
                grid-row: 3/4;
                grid-column: 1/3;
                &+div {
                    grid-row: 4/5;
                    grid-column: 3/5;
                }
            }
        }
    }
    
    main {
        & .title h1 {
            font-size: 3rem;
        }
        & .wrapperContent {
            & .line {
                margin: 0 20% 2rem 20%;
                padding-top: 4rem;
                grid-template-columns: repeat(8, 1fr);
            }

            // placement des éléments dans la grille
            & .img__line1 {
                grid-column: 1/5;
            }
            & .content__line1 {
                grid-column: 5/8;
            }
            & .dropdownArticle--line1 {
                grid-column: 1 / 9;
            }
            & .img__line2 {
                grid-column: 6/9;
            }
            & .content__line2 {
                grid-column: 2/5;
            }
            & .img__line3 {
                grid-column: 1/4;
            }
            & .content__line3 {
                grid-column: 5/8;
            }
            & .img__line4 {
                grid-column: 6/9;
            }
            & .content__line4 {
                grid-column: 2/5;
            }
        }
    }

    .banner {
        .wrapperTestLink {
            gap: 1rem;
            grid-template-columns: 3fr 1fr 3fr;

            // positionnement
            & .testFichier__button {
                margin: 3rem auto;
                grid-row: 2/3;
            }
            & .testLogiciel__button {
                margin: 3rem auto;
                grid-row: 2/3;
                grid-column: 3/4;
            }
        }
    }

    .footer {
        grid-template-columns: repeat(5, 1fr);
        text-align: left;
        & .containerLegal {
            grid-column: 2 / 3;
        }
        & .containerNousContacter {
            grid-column: 4 / 5;
        }
    }
}

